import React, {Component} from 'react';
import axios from 'axios';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";

import Show_products from './components/show_products'
import Add_product from './components/add_product'

export default class vendor_home extends Component {

    constructor(props) {
        super(props);

        this.state = {
            username: '',
            password: '',
        }

        this.onChangeUsername = this.onChangeUsername.bind(this);
        this.onChangePassword = this.onChangePassword.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }

    onChangeUsername(event) {
        this.setState({ username: event.target.value });
    }

    onChangePassword(event) {
        this.setState({ password: event.target.value });
    }

    onSubmit(e) {
        e.preventDefault();

        const newUser = {
            username: this.state.username,
            password: this.state.password,
        }
        // console.log(newUser)
        axios.post('http://localhost:4000/login', newUser)
             .then(res => {
                 console.log(res.data);
                 if(res.data.username == null){
                     alert("User not found");
                 }
                 else{
                     window.location('/vendor_home');
                 }
                });

        this.setState({
            username: '',
            password: '',
        });
    }

    onclick(){
        console.log(localStorage.user);
        console.log('fuck u')
    }


    render() {
        console.log("hello")
        return (
            <Router>
                <div>
                    <nav className="navbar navbar-expand-lg navbar-light bg-light">
                        <div className="collapse navbar-collapse">
                            <ul className="navbar-nav mr-auto">
                            <li className="navbar-item">
                                <Link to="/vendor_home" className="nav-link">Home</Link>
                            </li>
                            <li className="navbar-item">
                                <Link to="/show_products" className="nav-link">My Products</Link>
                            </li>
                            <li className="navbar-item">
                                <Link to="/add_products" className="nav-link">Add Products</Link>
                            </li>
                            </ul>
                        </div>
                    </nav>
                    <p> Welcome Vendor </p>
                    <p><button onclick='showdetails' type='button' id='demo' >My Profile</button> </p>
                    {/* <form onSubmit={this.onSubmit}>
                        <div className="form-group">
                            <label>Username: </label>
                            <input type="text" 
                                className="form-control" 
                                value={this.state.username}
                                onChange={this.onChangeUsername}
                                />
                        </div>
                        <div className="form-group">
                            <label>Password: </label>
                            <input type="password" 
                                className="form-control" 
                                value={this.state.password}
                                onChange={this.onChangePassword}
                                />  
                        </div>

                        <div className="form-group">
                            <input type="submit" value="Login" className="btn btn-primary"/>
                        </div>
                      </form> */}
                    <br/>
                    <Route path="/show_products" component={Show_products}/>
                    <Route path="/add_product" exact component={Add_product}/>
                </div>
            </Router>
        )
    }
}
